<script>
	import { fly } from 'svelte/transition';
	import { cubicIn, cubicOut } from 'svelte/easing';
	import { prompt, flag } from '$lib/stores';
	export let flagClicked = $flag;
</script>

<button
	on:click={() => {
		$flag = true;
	}}
>
	<img
		src="data:image/svg+xml;base64,"
		alt=""
		class="m-2 p-0 text-white top-8 w-8 left-16 inline-block {!flagClicked === false &&
			'grayscale'}"
	/>
</button>
<button
	on:click={() => {
		$flag = false;
	}}
>
	<img
		src="data:image/svg+xml;base64,"
		alt=""
		class="m-2 p-0 text-white top-8 w-8 left-16 inline-block {flagClicked === false && 'grayscale'}"
	/>
</button>
<div class="relative w-[600px] h-80 bg-sky-200 flex justify-center items-center rounded-xl">
	{#if $flag}
		<img
			src="data:image/svg+xml;base64,"
			alt="cartoon cat"
			in:fly={{ easing: cubicOut, y: 10, duration: 300, delay: 400 }}
			out:fly={{ easing: cubicIn, y: -10, duration: 300 }}
		/>
		<p
			class="max-w-48 right-0 select-none bg-white text-gray-600 border-gray-300 border-2 rounded-r-xl rounded-b-xl m-2 p-2 leading-tight"
			in:fly={{ easing: cubicOut, x: -10, duration: 300, delay: 800 }}
			out:fly={{ easing: cubicIn, y: -50, duration: 300 }}
		>
			{$prompt}
		</p>
	{/if}
</div>
